package eth1

import (
	"encoding/hex"
	"encoding/json"
	"strings"
	"testing"

	"github.com/bloxapp/ssv/eth1/abiparser"
	"github.com/bloxapp/ssv/utils/logex"

	"github.com/ethereum/go-ethereum/accounts/abi"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/pkg/errors"
	"github.com/stretchr/testify/require"
	"go.uber.org/zap"
)

func TestParseOperatorAddedEvent(t *testing.T) {
	var rawOperatorAdded = `{
  "address": "0x2EAD684aa2E10E31370830F00E0812bE6205F5f9",
  "topics": [
	"0xd839f31c14bd632f424e307b36abff63ca33684f77f28e35dc13718ef338f7f4",
	"0x0000000000000000000000003e6935b8250cf9a777862871649e5594be08779e"
  ],
  "data": "",
  "blockNumber": "0x6E1070",
  "transactionHash": "0x79478d46847aca9aa93f351c4b9c2126739a746b916da6445c0e64ab227fd016"
}`

	logger := logex.Build("test", zap.DebugLevel, nil)
	t.Run("operator added", func(t *testing.T) {
		LogOperatorAdded, contractAbi := unmarshalLog(t, rawOperatorAdded, V2)
		abiParser := NewParser(logger, V2)
		parsed, err := abiParser.ParseOperatorAddedEvent(*LogOperatorAdded, contractAbi)
		var malformedEventErr *abiparser.MalformedEventError
		require.NoError(t, err)
		require.False(t, errors.As(err, &malformedEventErr))
		require.NotNil(t, contractAbi)
		require.NotNil(t, parsed)
		require.Equal(t, "",
			string(parsed.PublicKey))
		require.Equal(t, "0x3e6935b8250Cf9A777862871649E5594bE08779e", parsed.Owner.Hex())
		require.Equal(t, uint64(1), parsed.Id)
	})
}

func TestParseValidatorAddedEvent(t *testing.T) {
	var rawValidatorAdded = `{
  "address": "0x2EAD684aa2E10E31370830F00E0812bE6205F5f9",
  "topics": [
	"0xd236b4a362a1dcc087f100d3c9de3d4dca2dbccf612505245ffa5444f6fc6ad0"
  ],
  "data": "",
  "blockNumber": "0x6E10A0",
  "transactionHash": "0x836169107c9e68eb9372daf220281b73552a6fcd99f188ca4335029d2513439d"
}`

	t.Run("v2 validator added", func(t *testing.T) {
		vLogValidatorAdded, contractAbi := unmarshalLog(t, rawValidatorAdded, V2)
		abiParser := NewParser(logex.Build("test", zap.InfoLevel, nil), V2)
		parsed, err := abiParser.ParseValidatorAddedEvent(*vLogValidatorAdded, contractAbi)
		var malformedEventErr *abiparser.MalformedEventError
		require.NoError(t, err)
		require.NotNil(t, contractAbi)
		require.False(t, errors.As(err, &malformedEventErr))
		require.NotNil(t, parsed)
		require.Equal(t, "ae27224346379f1cdc5ab4ff6fcd3841ce03f867f8c73fdef913a23508a4192a53e420d7affa56d3c37f48c26af6efb3", hex.EncodeToString(parsed.PublicKey))
		require.Equal(t, "0x3e6935b8250Cf9A777862871649E5594bE08779e", parsed.OwnerAddress.Hex())
		operators := []string{"", "", "", ""}
		for i, pk := range parsed.OperatorPublicKeys {
			require.Equal(t, operators[i], string(pk))
		}
		shares := []string{"b023c60eb5434b92551c523c48c2c5ffcdc0da8849bb7924d5651837747893c698aaf1c062ff912226fb88945ee551ec", "a5b7662004130babdc821e709d46ad55d90b4e326ad3ce6bb667ddb6cb7fbf1978f7aeefa86e6064875a7b0eb1d956a8", "aeb35e701a0909455b8e8a518f0b4f992db9f5146949e9be145479615657a522c0e0fd7a0a27883fd6dc8b8b6c7d1947", "b1e05cc9b7333e4392cc09ce7fea340de01e75fb1723d7572a3a37f181ff35df509aff40a110ad850e80305ef54c4989"}
		for i, pk := range parsed.SharePublicKeys {
			require.Equal(t, shares[i], hex.EncodeToString(pk))
		}
	})
}

func unmarshalLog(t *testing.T, rawOperatorAdded string, abiVersion Version) (*types.Log, abi.ABI) {
	var vLogOperatorAdded types.Log
	err := json.Unmarshal([]byte(rawOperatorAdded), &vLogOperatorAdded)
	require.NoError(t, err)
	contractAbi, err := abi.JSON(strings.NewReader(ContractABI(abiVersion)))
	require.NoError(t, err)
	require.NotNil(t, contractAbi)
	return &vLogOperatorAdded, contractAbi
}
